function fncReverse02(){let n="",t=document.getElementById("inputBox").value,i=t.length;for(let r=i-1;r>=0;r--)n+=t[r];return n}async function checkPalindrome(n){n.preventDefault();let r=document.getElementById("inputBox").value,i=document.getElementById("inputBox"),u=document.getElementById("outputBox"),t=document.getElementById("btnFlip");if(!r||r.trim()===""){i.classList.add("input-error");Swal.fire({title:"Empty Input!",text:"Please enter some text to check for palindromes.",icon:"warning"});return}t.disabled=!0;t.classList.add("loading");const e=t.textContent;t.textContent="Checking...";i.classList.remove("input-error");await new Promise(n=>setTimeout(n,300));let f=fncReverse02();u.value=f;t.disabled=!1;t.classList.remove("loading");t.textContent=e;r.toLowerCase()===f.toLowerCase()?(i.classList.add("input-success"),u.classList.add("input-success"),confetti({particleCount:100,spread:70,origin:{y:.6}}),Swal.fire({title:"You Haz Palindrome!",icon:"success"})):(i.classList.remove("input-success"),u.classList.remove("input-success"),Swal.fire({title:"Nope. Palindrome Failz...",icon:"error"}))}import"bootstrap";import"bootstrap/dist/css/bootstrap.min.css";import confetti from"canvas-confetti";import Swal from"sweetalert2";import"../styles/custom.css";document.getElementById("btnFlip").addEventListener("click",checkPalindrome);document.querySelector("#tacoContainer01 form").addEventListener("submit",checkPalindrome);document.getElementById("inputBox").addEventListener("keypress",function(n){n.key==="Enter"&&(n.preventDefault(),checkPalindrome(n))});document.getElementById("inputBox").addEventListener("input",function(){this.classList.remove("input-error","input-success");document.getElementById("outputBox").classList.remove("input-error","input-success")});